export function emailExistsValidator(members) {
  return function (control) {
    var email = control.value;
    if (members.length == 0) return null;
    var member = members.find(function (m) {
      return m.email === email;
    });
    return member ? {
      emailExists: true
    } : null;
  };
}
export function emailValidator(control) {
  var email = control.value;
  if (email && email.indexOf('@') !== -1 && email.indexOf('.', email.indexOf('@')) !== -1) {
    return null;
  } else {
    return {
      'invalidEmail': true
    };
  }
}
